import React, { useEffect, useState } from "react";
import { Button, StyleSheet, Text, View, Image, ImageBackground, Pressable } from "react-native";
import axios from 'axios'
import { TextInput } from "react-native-gesture-handler";
import {RadioGroup, RadioButton} from "react-native-flexi-radio-button"


export default function Test() {
  const URLs = 'http://localhost:8000/'
  const [news, onChangeNews] = React.useState(null);
  const [summary, setSummary] = React.useState('');
  const [selectedmode, setselectedmode] = React.useState(-1);

  const getData = async (bb) => {
      console.log(selectedmode)
      try {
          if (selectedmode == 0){
            const feedback = await axios.get(URLs + 'normal_summary?text=' + bb)
            console.log(feedback.data.summary)
            setSummary(feedback.data.summary)
          } else if (selectedmode == 1){
            const feedback = await axios.get(URLs + 'lexrank_summary?text=' + bb)
            console.log(feedback.data.summary)
            setSummary(feedback.data.summary)
          } else {
            console.log('@')
          }
      } catch(e) {
          console.log("fail")
      }
  }

  useEffect(() => {
      getData()
  }, [])

  return (
    <ImageBackground source={require('./assets/images/bg.png')} style={{width: '100%', height: '100%'}} resizeMode={"cover"}>
        <View style={{flex: 13, alignItems:'center'}}>
            {/* title */}
            <View style={{zIndex: 0, flex: 2, width:'100%', backgroundColor:'white', justifyContent:'center', alignItems:'center'}}>
                <Text style={styles.title}>NLP System</Text>
                <Text style={styles.title}>Summarization</Text>
            </View>
            {/* header */}
            <View style={styles.header}></View>
            {/* body */}
            <View style={styles.content}>
                <TextInput
                    // value = 'ooo' 
                    placeholder = "Please Input Your News Here ..."
                    placeholderTextColor = {'#BBBBBB'}
                    multiline
                    style = {[styles.input1, styles.footer2]}
                />
                <div style={{width:'10%'}}></div>
                <TextInput
                    // value = 'ooo' 
                    placeholder = "Your Summary"
                    placeholderTextColor = {'#BBBBBB'}
                    multiline
                    style = {[styles.input2, styles.footer2]}
                />
            </View>
            {/* footer */}
            <View style={{zIndex: 0, flex: 2, width:'100%', justifyContent:'flex-end', alignItems:'center'}}>
                <div style={{zIndex: 0, height:'50%'}}></div>
                <div style={{zIndex: 0, height:'50%', backgroundColor:'white', width:'100%',}}></div>
                <View style={[styles.footer1, styles.footer2]}>
                    <View style={{backgroundColor:'red', width:'20%', height:'20%'}}>
                        <Pressable style={{}} onPress={()=>{}}>
                        <Image 
                            style={{width:'80%', height:'80%'}}
                            source={require("./assets/images/arror.gif")}/>
                        </Pressable>
                    </View>
                </View>
            </View>
        </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
    title:{
        fontWeight: 'bold',
        fontFamily: "Impact",
        fontSize: 40  
    },
    header:{
        zIndex: 0, 
        width:'100%',
        flex: 1, 
        backgroundColor:'white', 
        borderColor:'#D3D3D3', 
        borderBottomWidth:1, 
        borderTopWidth: 1
    },
    content: {
        zIndex: 0, 
        flex: 8, 
        width:'100%', 
        justifyContent:'center', 
        alignItems:'center', 
        flexDirection:'row'
    },
    input1: {
        borderWidth:3,
        borderRadius:20,
        borderColor:'#9D858F',
        backgroundColor:'white',
        paddingVertical: 0, 
        paddingLeft: 5, 
        fontSize: 20, 
        lineHeight: 34,
        fontFamily: 'arial',
        height: '80%',
        width:'50%'
    },
    input2: {
        borderTopWidth:3,
        borderBottomWidth:3,
        borderRightWidth:3,
        borderLeftWidth:6,
        borderRadius:20,
        borderColor:'#9D858F',
        backgroundColor:'white',
        paddingVertical: 0, 
        paddingLeft: 5, 
        fontSize: 20, 
        lineHeight: 34,
        fontFamily: 'arial',
        height: '80%',
        width:'30%'
    },
    footer1: {
        backgroundColor:'white', 
        width:'80%', 
        height:'100%', 
        position: 'absolute', 
        zIndex:1
    },
    footer2: {
        shadowColor: 'black',
        shadowRadius: 10,
        shadowOpacity: 0.2
    }
})
