import React, { useEffect, useState } from "react";
import { Button, StyleSheet, Text, View, Image, ImageBackground } from "react-native";
import axios from 'axios'
import { TextInput } from "react-native-gesture-handler";
import {RadioGroup, RadioButton} from "react-native-flexi-radio-button"


export default function Test() {
  const URLs = 'http://localhost:8000/'
  const [news, onChangeNews] = React.useState(null);
  const [summary, setSummary] = React.useState('');
  const [selectedmode, setselectedmode] = React.useState(-1);

  const getData = async (bb) => {
      console.log(selectedmode)
      try {
          if (selectedmode == 0){
            const feedback = await axios.get(URLs + 'normal_summary?text=' + bb)
            console.log(feedback.data.summary)
            setSummary(feedback.data.summary)
          } else if (selectedmode == 1){
            const feedback = await axios.get(URLs + 'lexrank_summary?text=' + bb)
            console.log(feedback.data.summary)
            setSummary(feedback.data.summary)
          } else {
            console.log('@')
          }
      } catch(e) {
          console.log("fail")
      }
  }

  useEffect(() => {
      getData()
  }, [])

  return (
    <ImageBackground source={require('./assets/images/bg.png')} style={{width: '100%', height: '100%'}} resizeMode={"cover"}>
        <View style={{flex: 13, alignItems:'center'}}>
            {/* title */}
            <View style={{zIndex: 0, flex: 2, width:'100%', backgroundColor:'white', justifyContent:'center', alignItems:'center'}}>
                <Text style={styles.title}>NLP System</Text>
                <Text style={styles.title}>Summarization</Text>
            </View>
            {/* header */}
            <View style={styles.header}></View>
            {/* body */}
            <View style={{zIndex: 0, flex: 8, width:'100%',}}>
                <TextInput
                    // value = {summary} 
                    placeholderTextColor = {'#BBBBBB'}
                    multiline
                    // style = {styles.input}
                />
            </View>
            {/* footer */}
            <View style={{zIndex: 0, flex: 2, width:'100%', justifyContent:'flex-end', alignItems:'center'}}>
                <View style={{zIndex: 0, height:'50%'}}></View>
                <View style={{zIndex: 0, height:'50%', backgroundColor:'white', width:'100%',}}></View>
                <View style={[styles.footer1, styles.footer2]}></View>
            </View>
        </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
    title:{
        fontWeight: 'bold',
        fontFamily: "Impact",
        fontSize: 40  
    },
    header:{
        zIndex: 0, 
        width:'100%',
        flex: 1, 
        backgroundColor:'white', 
        borderColor:'#D3D3D3', 
        borderBottomWidth:1, 
        borderTopWidth: 1
    },
    input: {
        paddingVertical: 0, 
        paddingLeft: 5, 
        fontSize: 20, 
        lineHeight: 34,
        fontFamily: 'serif',
        height: '100%'
    },
    footer1: {
        backgroundColor:'white', 
        width:'80%', 
        height:'100%', 
        position: 'absolute', 
        zIndex:1
    },
    footer2: {
        shadowColor: 'black',
        shadowRadius: 10,
        shadowOpacity: 0.2
    }
})
