import React, { useEffect, useState } from "react";
import { Button, StyleSheet, Text, View, Dimensions } from "react-native";
import axios from 'axios'
import { TextInput } from "react-native-gesture-handler";

export default function App() {
  const URLs = 'http://localhost:8000/summary?text='
  const [news, onChangeNews] = React.useState(null);
  const [summary, setSummary] = React.useState('');

  const getData = async (bb) => {
      try {
          const feedback = await axios.get(URLs + bb)
          setSummary(feedback.data.summary)
      } catch(e) {
          console.log("fail")
      }
  }

  useEffect(() => {
      getData()
  }, [])

  return (
      <View style={{alignItems:'center', justifyContent:'center', flex:12}}>

        <View style = {{flex:2, justifyContent: 'center'}}>
          <Text style = {{fontSize: 60, fontWeight: "bold", fontFamily: 'serif'}}> News Summarizer </Text>
        </View>

        <View style={{flex:10, width:'90%', flexDirection:'row', alignItems:'center'}}>
          {/* <View style = {styles.container} > */}
          <View style = {[styles.a, styles.b]} >
            <View style = {styles.inputContainer}>
              <TextInput
                  placeholder = {'Input Your News'} 
                  placeholderTextColor = {'#BBBBBB'}
                  onChangeText = {text => onChangeNews(text)}
                  multiline
                  style = {styles.input}
              />
            </View>      
          </View>

          <View style={{flex:2}}><iframe src="https://giphy.com/embed/rDb9zTgdfiPwQ" width="480" height="279" frameBorder="0" class="giphy-embed" allowFullScreen></iframe><p><a href="https://giphy.com/gifs/arrow-rDb9zTgdfiPwQ">via GIPHY</a></p></View>

          <View style = {[styles.a, styles.b]} >
            <View style = {styles.inputContainer}>
              <TextInput
                  value = {summary} 
                  placeholderTextColor = {'#BBBBBB'}
                  multiline
                  style = {styles.input}
              />
            </View>      
          </View>
        </View>

        <View style = {{flex:1, justifyContent: 'center'}}>
          <Button
              color='grey' 
              title='Generate Summary'
              onPress = {() => {getData(news);}}
          />
        </View>

        <View style = {{flex:1}}></View>

      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex:4,
    justifyContent: 'center',
    width: '50%',
    height: '30%',
    alignItems: 'center',
    paddingHorizontal: 15,
    paddingBottom: 0
  },
  inputContainer: {
    height: '90%',
    width: '100%',
    borderWidth: 2,
    borderColor: '#F6ECEA'
  },
  input: {
    paddingVertical: 0, 
    paddingLeft: 5, 
    fontSize: 20, 
    lineHeight: 34,
    fontFamily: 'serif',
    height: '100%'
  },
  a: {
    flex:4, 
    justifyContent: 'center',
    width: '50%',
    height: '90%',
    alignItems: 'center',
    paddingHorizontal: 15,
    paddingBottom: 0,
    flexDirection:'colunm', 
    backgroundColor:'#F9F9DB', 
    borderRadius:20,
  }, 
  b: {
      shadowColor: 'black',
      shadowRadius: 5,
      shadowOpacity: 0.5,
      shadowOffset: {width: 0, height:0},
  }
});
