import React, { useEffect, useState } from "react";
import { Button, StyleSheet, Text, View, Dimensions } from "react-native";
import axios from 'axios'
import { TextInput } from "react-native-gesture-handler";

export default function App() {
  const URLs = 'http://localhost:8000/tag?text='
  const [news, onChangeNews] = React.useState(null);

  const getData = async (bb) => {
      try {
          console.log(bb)
          const feedback = await axios.get(URLs + bb)
          console.log(feedback.data.tags)
      } catch(e) {
          console.log("fail")
      }
  }

  useEffect(() => {
      getData()
  }, [])

  return (
      <View style={{alignItems:'center', justifyContent:'center', flex:12}}>

        <View style = {{flex:2}}></View>

        <View style = {styles.container} >
          <View style = {styles.inputContainer}>
            <TextInput
                placeholder = {'Input Your News'} 
                placeholderTextColor = {'#BBBBBB'}
                // underlineColorAndroid = {'transparent'} 
                multiline
                style = {styles.input}
            />
          </View>      
        </View>

        <View style = {{flex:1, justifyContent: 'center'}}>
          <Button
              color='grey' 
              title='REFRESH BBOARDS'
              onPress = {() => {getData('asd jkahsdkjh asd ');}}
          />
        </View>

        <View style = {styles.container} >
          <View style = {styles.inputContainer}>
            <TextInput
                placeholder = {'Input Your News'} 
                placeholderTextColor = {'#BBBBBB'}
                // underlineColorAndroid = {'transparent'} 
                multiline
                style = {styles.input}
            />
          </View>      
        </View>

        <View style = {{flex:1}}></View>
          {/* <View style={{backgroundColor: 'red', flex:5}}>
            <Text>{news}</Text>
          </View> */}
      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex:4,
    justifyContent: 'center',
    // backgroundColor: 'red',
    width: '50%',
    height: '30%',
    alignItems: 'center',
    paddingHorizontal: 15,
    paddingBottom: 0
  },
  inputContainer: {
    height: '90%',
    width: '100%',
    borderWidth: 1,
    borderColor: '#eeeeee'
  },
  input: {
    paddingVertical: 0, 
    paddingLeft: 5, 
    fontSize: 16, 
    height: '100%'
  }
});
